(1038, 'MTUwNTg2MTMwMzoxNTY0MDoy', 'Habitat', 'Occurs in rich woods throughout eastern and western Miami lands', 'Gleason and Cronquist 1991', '1991', '', '', 0, 1, 'Gleason and Cronquist 1991')
(1039, 'MTUwNTg2MTMwMzoxNTY0MDoy', 'Use - Medicinal', 'Ginseng (pilo\'sa) used to produce fruitfulness in women, taken as a tea or decoction pilosapo\'I', 'Dunn 1900', '1900', '', '', 0, 0, 'Dunn ca. 1900:Indiana State Library L47, Note #35')
(1040, 'MTUwNTg2MTMwMzoxNTY0MDoy', 'Use - Technology (trade)', 'collected and dried, then sold for pharmaceutical use', 'Rafert 1992', '1992', '', '', 0, 0, 'Rafert 1992:182')
(1041, 'MTUwNTg2MTMwMzoxNTY0MDoy', 'Use - Medicinal', '\"abesoatchenza\", ginseng root used to make women fruitful', 'Charlevoix 1682-1761', '1682-1761', '', '', 0, 0, 'Charlevoix 1682-1761:99, from Kellogg 1923:93')
(1042, 'MTUwNTg2MTMwMzoxNTY0MDoy', 'Use - Medicinal', 'harvested for use', 'Tippman 2005', '2005', '', '', 0, 0, 'Interview with Dani Tippman, Gonella 2005')
